using System.Threading.Tasks;

using BootstrapMvc.UI.ViewModels;

using Microsoft.AspNetCore.Mvc;

namespace BootstrapMvc.UI.ViewComponents
{
    public class ImageDisplay : ViewComponent
    {
        public async Task<IViewComponentResult> InvokeAsync(ImageViewModel image)
        {
            return View("ImageDisplay", await Task.FromResult(image ?? GetLogo()));
        }

        private static ImageViewModel GetLogo()
        {
            return new ImageViewModel { Base64 = "data:image/jpeg;base64," };
        }
    }
}
